;--------------------------------------------------------------------
; subroutine to clear the screen
;--------------------------------------------------------------------
clrscr:		push es
			push ax
			push di

			mov ax, 0xb800
			mov es, ax					; point es to video base
			mov di, 0					; point di to top left column
			mov ah, 0x07
			mov al, 0x20

nextloc:	mov word [es:di], ax	; clear next char on screen
			;inc AL
			add di, 2					; move to next screen location
			cmp di, 10;11352				; 132x43x2
			jne nextloc					; if no clear next position

			pop di
			pop ax
			pop es
			ret
;--------------------------------------------------------------------
; subroutine to print a string at top left of screen
; takes address of string and its length as parameters
;--------------------------------------------------------------------
printstr:	push bp
			mov bp, sp
			push es
			push ax
			push cx
			push si
			push di
			push bx

            mov bx, 4780
			sub bx, 264
			mov ax, 0xb800
			mov es, ax				; point es to video base
			mov di, bx			; point di to top left column						; es:di --> b800:0000
			mov si, [bp+6]			; point si to string
			sub si,1 
			mov cx, [bp+4]			; load length of string in cx
			mov ah, 0x07			; normal attribute fixed in al

nextline:
         add bx, 264
		 mov di, bx	
         add si,1		 
nextchar:	mov al, [si]			; load next char of string
            cmp al, '0'
			je nextline
			mov [es:di], ax			; show this char on screen
			add di, 2				; move to next screen location
			add si, 1				; move to next char in string			
			loop nextchar			; repeat the operation cx times
			
			pop bx
			pop di
			pop si
			pop cx
			pop ax
			pop es
			pop bp
			ret 4

;--------------------------------------------------------------------
